import styled from "styled-components";

export const Wrapper = styled.div`
  height: 350px;
  width: 450px;
  color: #5c7eec;
  background: #e9c697;
  font-size: 24px;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 4px;
`;
