  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-around;
`;

export const AddHeader = styled.h1`
  font-size: 24px;
  align-text: center;
  margin-bottom: 24px;
`;

export const CartImage = styled.img`
  width: 60%;
`;

export const CartWrapper = styled.div`
