import React, { useEffect, useState } from "react";
import {
  Wrapper,
  AddHeader,
  CartImage,
  AddWrapper,
  CartWrapper,
  CartHeader,
  RoomTitle,
  RoomPrice,
  RoomDetails,
  RoomImage,
  ButtonWrapper,
} from "./styles";
import { cartPng } from "../../assets";
import { useRecoilState } from "recoil";
import atoms from "../../atoms";
import { Button } from "../index";

const Cart = () => {
  const [cart] = useRecoilState(atoms.cart);
  const getTaxesFees = () => {
    return cart.price * 0.08;
  };
  const getTotal = () => {
    return cart.price + cart.price * 0.08;
  };
  return (
    <Wrapper>
      {cart ? (
        <CartWrapper>
          <CartHeader>Your selection...</CartHeader>
          <RoomImage src={cart.image} />
          <RoomTitle>{cart.title}</RoomTitle>
          <RoomDetails>5 Star Breakfast Included</RoomDetails>
          <RoomPrice>
            <div>Cost Per Night:</div> <div>${cart.price}</div>
          </RoomPrice>
          <RoomPrice>
            <div>Taxes / Fees:</div> <div>${getTaxesFees()}</div>
          </RoomPrice>
          <RoomPrice>
            <div>Total:</div> <div>${getTotal()}</div>
          </RoomPrice>
          <ButtonWrapper>
            <Button
              width={"100%"}
              text={"Checkout"}
              border={"1px solid white"}
              backgroundColor={"#f6a4eb"}
            />
          </ButtonWrapper>
        </CartWrapper>
      ) : (
        <AddWrapper>
          <AddHeader>Your cart is empty</AddHeader>
          <CartImage src={cartPng} />
        </AddWrapper>
      )}
    </Wrapper>
  );
};

export default Cart;
