import React, { useEffect, useState } from "react";
import {
  Wrapper,
  Header,
  Body,
  RoomImage,
  DetailWrap,
  DetailItem,
  PriceWrap,
} from "./styles";
import { Button } from "../index";

const OpenPanel = ({ data }) => {
  const { title, image, body, price } = data;
  return (
    <Wrapper>
      <Header>{title}</Header>
      <Body>
        <RoomImage src={image} />
        <DetailWrap>
          {body.map((item, idx) => (
            <DetailItem key={idx}>{item}</DetailItem>
          ))}
        </DetailWrap>
        <PriceWrap>{`$${price}`} per night</PriceWrap>
      </Body>
    </Wrapper>
  );
};

export default OpenPanel;
