import styled from "styled-components";

export const Wrapper = styled.div`
  width: 450px;
  background: white;
  border-radius: 4px;
  padding: 20px;
`;

export const Body = styled.div`
  text-align: center;
`;

export const ButtonWrapper = styled.div`
  margin-top: 24px;
  display: flex;
  padding: 0px 25px;
  flex-direction: row;
  justify-content: space-around;
`;
