import React, { useEffect, useState } from "react";

import {
  Wrapper,
  AddHeader,
  CartImage,
  AddWrapper,
  CartWrapper,
  CartHeader,
  RoomTitle,
  RoomPrice,
  RoomDetails,
  RoomImage,
} from "./styles";
import { cartPng } from "../../assets";
import { useRecoilState } from "recoil";
import atoms from "../../atoms";
const Cart = () => {
  const [cart] = useRecoilState(atoms.cart);
  console.log(cart, "-------c");
  getTaxesFees = () => {
    return cart.price * 0.08;
  };
  return (
    <Wrapper>
      {cart ? (
        <CartWrapper>
          <CartHeader>Your selection...</CartHeader>
          <RoomImage src={cart.image} />

          <RoomTitle>{cart.title}</RoomTitle>
          <RoomDetails>5 Star Breakfast Included</RoomDetails>
          <RoomPrice>${cart.price} per night</RoomPrice>
          <RoomPrice>Taxes / Fees: {getTaxesFees}</RoomPrice>
        </CartWrapper>
      ) : (
        <AddWrapper>
          <AddHeader>Your cart is empty</AddHeader>
          <CartImage src={cartPng} />
        </AddWrapper>
      )}
    </Wrapper>
  );
};

export default Cart;
